import React, { useState } from 'react';

function PersonalDetails({ user }) {
  const [profileImage, setProfileImage] = useState(user?.profileImage || 'https://via.placeholder.com/150');
  const [uploading, setUploading] = useState(false);

  if (!user) return <div>Loading...</div>;

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Create a local URL for the uploaded image
      const reader = new FileReader();
      reader.onloadend = () => {
        setProfileImage(reader.result);
        // Here you would normally upload to server
        // For now, we'll just store it in localStorage
        localStorage.setItem('profileImage', reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="content-card">
      <h2>Personal Details</h2>
      
      <div className="personal-info">
        <div className="profile-image-container">
          <img src={profileImage} alt={user.name} className="profile-img" />
          
          <label htmlFor="profile-upload" className="upload-btn">
            ðŸ“· Change Photo
          </label>
          <input
            id="profile-upload"
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            style={{ display: 'none' }}
          />
        </div>

        <div className="info-grid">
          <div className="info-item">
            <label>Name:</label>
            <span>{user.name}</span>
          </div>

          <div className="info-item">
            <label>Email:</label>
            <span>{user.email}</span>
          </div>

          <div className="info-item">
            <label>Phone:</label>
            <span>{user.phone}</span>
          </div>

          <div className="info-item">
            <label>Role:</label>
            <span className="role-badge">{user.role}</span>
          </div>
        </div>
      </div>
    </div>
  );
}

export default PersonalDetails;
